function AddToCart(n,t,i,r){$.ajax({type:"GET",contentType:"application/json; charset=utf-8",url:"/Cart/AddToCart/"+n+"/"+i+"/"+r,success:function(n){if(n.length>0){var i=JSON.parse(n),r="<strong>"+t+'<\/strong> Added to <a href="/cart">Cart<\/a> Successfully!';$("#CartCounter").text(i.CartItems.length);$("#ToastCart > .toast-body").html(r);$("#ToastCart").show();setTimeout(function(){$("#ToastCart").toast("hide")},4e3)}},error:function(n,t,i){console.error("Error: "+i);console.error("Status: "+t);console.error(n.responseText);alert("Failed to add item to cart. Please try again.")}})}function DeleteItem(n){n>0&&$.ajax({type:"GET",contentType:"application/json; charset=utf-8",url:"/Cart/Delete/"+n,success:function(n){n>=0&&location.reload()},error:function(){}})}function UpdateQuantity(n,t,i){n>0&&i>0?$.ajax({type:"GET",contentType:"application/json",url:"/Cart/UpdateQuantity/"+n+"/"+i,success:function(n){n>0&&location.reload()},error:function(){console.error("Failed to update quantity.")}}):n>0&&i<0&&t>1&&$.ajax({type:"GET",contentType:"application/json",url:"/Cart/UpdateQuantity/"+n+"/"+i,success:function(n){n>0&&location.reload()},error:function(){console.error("Failed to update quantity.")}})}$(document).ready(function(){$.ajax({type:"GET",contentType:"application/json; charset=utf-8",url:"/Cart/GetCartCount",dataType:"json",success:function(n){$("#CartCounter").text(n)},error:function(){}})});